import React, { useState } from "react";

function AdominLogin() {
  const [userName, setUserName] = useState(""); // ユーザー名の state
  const [Password, setPassword] = useState(""); // パスワードの state

  const handleSubmit = async (e) => {
    e.preventDefault();

    // フォームデータの作成
    const loginData = {
      userName,
      Password,
    };

    try {
      // APIリクエストの送信
      const response = await fetch("http://localhost:5274/api/Adomin/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(loginData), // フォームデータを送信
      });

      if (!response.ok) {
        // エラーレスポンスの処理
        const errorResponse = await response.json();
        console.log(errorResponse.message);
      } else {
        // 成功時の処理
        console.log("ログインに成功しました");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      <h2>ログイン</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text" // メールアドレスではなくユーザー名として扱う
          placeholder="ユーザー名"
          value={userName} // ユーザー名の値を設定
          onChange={(e) => setUserName(e.target.value)} // ユーザー名の入力処理
          required // フォーム入力の必須化
        />
        <input
          type="password"
          placeholder="パスワード"
          value={Password} // パスワードの値を設定
          onChange={(e) => setPassword(e.target.value)} // パスワードの入力処理
          required // フォーム入力の必須化
        />
        <button type="submit">ログイン</button>
      </form>
    </div>
  );
}

export default AdominLogin;
