import React, { useState } from "react";
import axios from "axios";

function AdominLogin() {
  const [userName, setUserName] = useState(""); // ユーザー名の state
  const [Password, setPassword] = useState(""); // パスワードの state

  const handleSubmit = async (e) => {
    e.preventDefault();

    // フォームデータの作成
    const loginData = {
      userName,
      Password,
    };

    try {
      // APIリクエストの送信
      const response = await axios.post(
        "http://localhost:5274/Adomin/login",
        loginData
      );

      // レスポンスの確認
      console.log("Response:", response);

      if (response.data) {
        console.log("ログインに成功しました", response.data);
        // 成功時の処理をここに追加
      } else {
        console.error("No data received.");
      }
    } catch (error) {
      console.error("Error occurred:", error);

      if (error.response) {
        // サーバーが応答を返したがエラーコードを含む場合
        console.error("Error response:", error.response.data);
      } else if (error.request) {
        // リクエストは送信されたが、応答がない場合
        console.error("Error request:", error.request);
      } else {
        // その他のエラー
        console.error("Error:", error.message);
      }
    }
  };

  return (
    <div>
      <h2>ログイン</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text" // メールアドレスではなくユーザー名として扱う
          placeholder="ユーザー名"
          value={userName} // ユーザー名の値を設定
          onChange={(e) => setUserName(e.target.value)} // ユーザー名の入力処理
          required // フォーム入力の必須化
        />
        <input
          type="password"
          placeholder="パスワード"
          value={Password} // パスワードの値を設定
          onChange={(e) => setPassword(e.target.value)} // パスワードの入力処理
          required // フォーム入力の必須化
        />
        <button type="submit">ログイン</button>
      </form>
    </div>
  );
}

export default AdominLogin;
